/*config loading*/
import Config from '../config/app';

/*lang loading*/
import zh_cn from './i18n/zh_cn';
import zh_tw from './i18n/zh_tw';
import de from './i18n/de';
import en from './i18n/en';
import fr from './i18n/fr';
import jp from './i18n/jp';

function Lang(type,ct) {
    const Langs = {
        'zh_cn' : zh_cn,
        'zh_tw' : zh_tw,
        'de' : de,
        'en' : en,
        'fr' : fr,
        'jp' : jp,
    };

    let langtype = Storage.lang ? Storage.lang : 'zh_cn';

    return Langs[langtype][type][ct];
}

export { Lang , Config }
