import React, { Component } from 'react';
import './main.scss';

function Maketextarea(props) {
    return <textarea name={props.name} placeholder={props.default}>{props.value}</textarea>;
}

function Makeinput(props) {
    let key = 0;
    let html = [];
    let arr = props.arr;
    for (let name in arr){
        key ++;
        let data = arr[name];
        if(data.type === 'textarea'){
            html[key] = <textarea name={name} placeholder={data.default}>{data.value}</textarea>;
        } else if (data.type === 'sec') {
            html[key] = '';
        } else {
            html[key] = <input type={data.type} name={name} value={data.value} placeholder={data.default} />;
        }
    }

    return html;
}

export default class Form extends Component {
    render() {
        const Mid = this.props.forminit;
        return (
            <form className={Mid.class} method={Mid.method}  >
                <input name="token" type="hidden" value={Mid.token} />
                <input name="hash" type="hidden" value={Mid.hash} />
                <Makeinput arr={Mid.inputs} />
            </form>
        );
    }
}
