import React, {Component} from 'react';
import './main.scss';

function Maketextarea(props) {
    return <textarea
        name={props.name}
        placeholder={props.default}
    >{props.value}</textarea >;
}

function Makeinput(props) {
    return <input
        type={props.type}
        name={props.name}
        value={props.value}
        placeholder={props.default}
    />;
}


function Makef(props) {
    let html = [];
    let arr = props.arr;
    for (let i = 0; i < arr.length; i++) {
        let data = arr[i];
        if (data.type === 'textarea') {
            html[i] = <Maketextarea name={data.name} placeholder={data.default} value={data.value} />;
        } else if (data.type === 'sec') {
            html[i] = '';
        } else {
            html[i] = <Makeinput name={data.name} placeholder={data.default} value={data.value} />;
        }
    }

    return html;
}

export default class Form extends Component {
    render() {
        const Mid = this.props.forminit;
        return (
            <form className={Mid.class} method={Mid.method} >
                <input name="token" type="hidden" value={Mid.token} />
                <input name="hash" type="hidden" value={Mid.hash} />
                <Makef arr={Mid.inputs} />
            </form >
        );
    }
}
