/***************\
  config loading
\***************/
import Config from './config/config';

/***************\
  lang loading
\***************/
import zh_cn from './i18n/zh_cn';
import zh_tw from './i18n/zh_tw';
import de from './i18n/de';
import en from './i18n/en';
import fr from './i18n/fr';
import jp from './i18n/jp';
const
    Langs = {
        'zh_cn': zh_cn,
        'zh_tw': zh_tw,
        'de': de,
        'en': en,
        'fr': fr,
        'jp': jp,
    },
    Lang = Langs[Storage.lang ? Storage.lang : Config.lang]
;

/***************\
 calc length GBK
\***************/
function strlen(val) {
    let len = 0;
    for (let i = 0; i < val.length; i++) {
        var a = val.charAt(i);
        if (a.match(/[^\x00-\xff]/ig) != null) {
            len += 2;
        }
        else {
            len += 1;
        }
    }
    return len;
}

/***************\
 create rand str
\***************/

function rand(length, type){
    let arr = [] , str = '';

    const
        num = '0123456789',
        letM = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ',
        letm = 'abcdefghijklmnopqrstuvwxyz'
    ;

    if(type === 'num'){
        arr = num;
    } else if(type === 'letter'){arr = letM;
    } else if(type === 'mixm'){arr = num + letm;
    } else if(type === 'mixM'){arr = num + letM;
    } else {arr = num + letm + letM;
    }
    for(let i=0; i < length; i++){
        str += arr[Math.round(Math.random() * (arr.length-1))];
    }
    return str;
}

/***************\
      Output
\***************/
export {Config, Lang, Langs, rand, strlen}
