import React, {Component} from 'react';
import './main.scss';
import {Lang, strlen} from '../../helper';

function Maketextarea(props) {
    let data = props.data;
    let ComponentChange = function (e) {
        let counter =  e.target.parentNode.querySelector('.counter');
        if(counter) counter.innerHTML = strlen(e.target.value);
    };
    return (
        <div className="components-textarea">
            {data.icon}
            <textarea name={data.name} placeholder={data.default} defaultValue={data.value} onChange={ComponentChange}>{}</textarea >
            {data.check}<span className="counter">{strlen(data.value)}</span>
        </div>
    );
}

function Makeinput(props) {
    let data = props.data;
    let ComponentChange = function (e) {
        let counter =  e.target.parentNode.querySelector('.counter');
        if(counter) counter.innerHTML = strlen(e.target.value);
    };
    return (
        <div className="components-input">
            {data.icon}
            <input type={data.type && 'text'} name={data.name} defaultValue={data.value} placeholder={data.default} onChange={ComponentChange}/>
            {data.check}<span className="counter">{strlen(data.value)}</span>
        </div>
    );
}

function Makeselect(props) {
    let order = 0;
    let data = props.data;
    let ComponentChange = function (e) {};
    return (
        <div className="components-select">
            {data.icon}
            <select name={data.name} defaultValue={data.default} onChange={ComponentChange}>
                {data.opts.map((op) => <option key={order++} disabled={op.disable && /disabled/} value={op.value} >{op.desc}</option >)}
            </select >
            {data.check}
        </div>
    );
}

export default class Form extends Component {



    render() {

        Mid = this.props.forminit;
        submit = this.props.submit;
        change = this.props.change;



        const formSubmit = function(e) {
            e.map = {
                API: this.Mid.action,
                method: this.Mid.method,
                data: new FormData(e.target),
            };
            e.map.data.append('token', Storage.token);
            e.map.data.append('hash', Storage.hash);
            this.submit(e)
        };

        const formChange = function(e) {
            const target = e.target;
            const name = target.name;
            const value = target.value;

            this.change(e);
            this.setState({
                [name] : value,
            });
        };

        const ComponentFill = function (props) {
            const Makeitem = function (props) {
                let data = props.data;
                switch (data.type) {
                    case 'textarea': return <Maketextarea data={data} />;
                    case 'select': return <Makeselect data={data} />;
                    case 'sec': break;
                    default : return <Makeinput data={data} />;
                }
            };
            let order = 0;
            return props.data.map((item) => <Makeitem key={order++} data={item} />);
        };

        return (
            <form className={this.Mid.class} onSubmit={formSubmit} onChange={formChange}>
                <ComponentFill data={this.Mid.inputs} />
                <div className="children" >{this.Mid.children}</div >
                <div>{this.state.work}</div>
                <button className="submit" type="submit" >{Lang.common.submit}</button >
            </form >
        );
    }
}
