/*lang loading*/
import zh_cn from './i18n/zh-cn';
import zh_tw from './i18n/zh-tw';
import de from './i18n/de';
import en from './i18n/en';
import fr from './i18n/fr';
import jp from './i18n/jp';


function Lang(type,ct) {
    const Langs = {
        'zh-cn' : zh_cn,
        'zh-tw' : zh_tw,
        'de' : de,
        'en' : en,
        'fr' : fr,
        'jp' : jp,
    };
    let storage = window.localStorage;
    storage.lang = 'en';
    let langtype = storage.lang ? storage.lang : 'zh-cn';



    console.log(storage);
    return Langs[langtype][type][ct];
}

export {Lang,}
