import React, { Component } from 'react';
import './main.scss';

function Makeinput(props) {
    let html;
    let arr = props.arr;
    for (let name in arr){

        let data = arr[name];
        let e;

        if(data.type === 'textarea'){
            e = document.createElement('textarea');
        } else if (data.type === 'sec') {

        } else {
            e = document.createElement('input');
            e.type = data.type ? data.type : '';
            e.value = data.value ? data.value : '';
        }
        e.name = name;
        e.placeholder = data.default ? data.default : '';

        html += JSON.stringify(e);
    }

    return html;
}

export default class Form extends Component {
    render() {

        const Mid = this.props.forminit;

        return (
            <form className={Mid.class} method={Mid.method}  >
                <input name="token" type="hidden" value={Mid.token} />
                <input name="hash" type="hidden" value={Mid.hash} />
                <Makeinput arr={Mid.inputs} />
            </form>
        );
    }
}
