import React, {Component} from 'react';
import './main.scss';
import {Lang} from '../../helper';

function Maketextarea(props) {
    let data = props.data;
    let ComponentChange = function (e) {
        let counter =  e.target.parentNode.querySelector('.counter');
        if(counter) counter.innerHTML = e.target.value.length;
    };
    return (
        <div className="components-textarea">
            {data.icon}
            <textarea name={data.name} placeholder={data.default} defaultValue={data.value} onChange={data.change && ComponentChange}>
            </textarea >
            {data.check}
            <span className="counter">
            </span>
        </div>
    );
}

function Makeinput(props) {
    let data = props.data;
    let ComponentChange = function (e) {
        let counter =  e.target.parentNode.querySelector('.counter');
        if(counter) counter.innerHTML = e.target.value.length;
    };
    return (
        <div className="components-input">
            {data.icon}
            <input type={data.type && 'text'} name={data.name} defaultValue={data.value} placeholder={data.default} onChange={data.change && ComponentChange}/>
            {data.check}
            <span className="counter">
            </span>
        </div>
    );
}

function Makeselect(props) {
    let order = 0;
    let data = props.data;
    let ComponentChange = function (e) {};
    return (
        <div className="components-select">
            {data.icon}
            <select name={data.name} defaultValue={data.default} onChange={data.change && ComponentChange}>
                {data.opts.map((op) => <option key={order++} disabled={op.disable && /disabled/} value={op.value} >{op.desc}</option >)}
            </select >
            {data.check}
        </div>
    );
}

export default class Form extends Component {
    render() {

        let Mid = this.props.forminit;
        let Submit = this.props.submit;
        let Change = this.props.change;

        let ComponentSubmit = function (e) {
            e.preventDefault();
            let form = e.target;
            e.data = new FormData(form);
            e.data.append('token', Storage.token);
            e.data.append('hash', Storage.hash);
            Submit(e);
        };
        let ComponentChange = function (e) {
            Change(e);
        };

        let ComponentFill = function (props) {
            let Makeitem = function (props) {
                let data = props.data;
                if (data.type === 'textarea') {
                    return <Maketextarea data={data} />;
                } else if (data.type === 'sec') {

                } else if (data.type === 'select') {
                    return <Makeselect data={data} />;
                } else {
                    return <Makeinput data={data} />;
                }
            };
            let order = 0;
            return props.data.map((item) => <Makeitem key={order++} data={item} />);
        };

        return (
            <form className={Mid.class} method={Mid.method} action={Mid.action} onSubmit={ComponentSubmit} onChange={ComponentChange} encType="multipart/form-data" >
                <ComponentFill data={Mid.inputs} />
                <div className="children" >{Mid.children}</div >
                <button className="submit" type="submit" >{Lang.common.submit}</button >
            </form >
        );
    }
}
