import React, {Component} from 'react';
import './main.scss';

function Maketextarea(props) {
    return (
        <textarea name={props.name} placeholder={props.default} defaultValue={props.value}>
        </textarea >
    );
}

function Makeinput(props) {
    return (
        <input type={props.type && 'text'} name={props.name} defaultValue={props.value} placeholder={props.default} />
    );
}

function Makeselect(props) {
    let order = 0;
    return (
        <select name={props.name}>
            {props.opts.map((op) =>
                <option key={order++} disabled={op.disable && /disabled/} selected={op.select && /selected/} value={op.value} >{op.desc}</option>
            )}
        </select>
    );
}

export default class Form extends Component {

    render() {
        function Makef(props) {
            let order = 0;
            let arr = props.arr;
            let Makeitem = function (propts) {
                let data = propts.item;
                if (data.type === 'textarea') {
                    return <Maketextarea name={data.name} placeholder={data.default} value={data.value} />;
                } else if (data.type === 'sec') {

                } else if (data.type === 'select') {
                    return <Makeselect name={data.name} opts={data.opts} />;
                } else {
                    return <Makeinput name={data.name} placeholder={data.default} value={data.value} />;
                }
            };

            return arr.map((item)=> <Makeitem key={order++} item={item} />);
        }

        const Mid = this.props.forminit;

        return (
            <form className={Mid.class} method={Mid.method} >
                <input name="token" type="hidden" value={Mid.token} />
                <input name="hash" type="hidden" value={Mid.hash} />
                <Makef arr={Mid.inputs} />
            </form >
        );
    }
}
