import React, { Component } from 'react';
import './main.scss';

function Makeinput(arr) {
    let html = '';
    for (let name in arr){
        if(arr[name].type === 'textarea'){
            html += '<textarea placeholder="' + arr[name].default + '">'+ arr[name].value +'</textarea>';
        } else if (arr[name].type === 'sec') {

        } else {
            html += '<input type="'+ arr[name].type +'" name="'+ name +'" value="'+ arr[name].value +'" placeholder="'+ arr[name].default +'" />';
        }
    }

    return html;
}

export default class Form extends Component {
    render() {

        const Mid = this.props.forminit;

        return (
            <form className={Mid.class} method={Mid.method}  >
                <input name="token" type="hidden" value={Mid.token} />
                <input name="hash" type="hidden" value={Mid.hash} />
                {Makeinput(Mid.inputs)}
            </form>
        );
    }
}
