import React, {Component} from 'react';
import './main.scss';
import {Lang} from '../../helper';

function Maketextarea(props) {
    let data = props.data;
    return (
        <textarea name={data.name} placeholder={data.default} defaultValue={data.value} >
        </textarea >
    );
}

function Makeinput(props) {
    let data = props.data;
    return (
        <input type={data.type && 'text'} name={data.name} defaultValue={data.value} placeholder={data.default} />
    );
}

function Makeselect(props) {
    let order = 0;
    let data = props.data;
    return (
        <select name={data.name} defaultValue={data.default} >
            {data.opts.map((op) => <option key={order++} disabled={op.disable && /disabled/} value={op.value} >{op.desc}</option >)}
        </select >
    );
}

export default class Form extends Component {
    render() {

        const Mid = this.props.forminit;
        const Act = this.props.onSubmit;

        let ComponentSubmit = function (e) {
            e.preventDefault();
            e.data = new FormData(this);
            Act(e);
        };

        let ComponentFill = function (props) {
            let Makeitem = function (props) {
                let data = props.data;
                if (data.type === 'textarea') {
                    return <Maketextarea data={data} />;
                } else if (data.type === 'sec') {

                } else if (data.type === 'select') {
                    return <Makeselect data={data} />;
                } else {
                    return <Makeinput data={data} />;
                }
            };
            let order = 0;
            return props.data.map((item) => <Makeitem key={order++} data={item} />);
        }

        return (
            <form className={Mid.class} method={Mid.method} action={Mid.action} onSubmit={ComponentSubmit} encType="multipart/form-data" >
                <input name="token" type="hidden" value={Mid.token} />
                <input name="hash" type="hidden" value={Mid.hash} />
                <ComponentFill data={Mid.inputs} />
                <button type="submit">{Lang.common.submit}</button>
            </form >
        );
    }
}
