import React, {Component} from 'react';
import './main.scss';
import {Lang, strlen, substr} from '../../helper';

class Textarea extends Component{

    constructor(props) {
        super(props);
        this.state = {[props.data.name] : props.data.value};
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        this.setState({
            [this.props.data.name]: substr(e.target.value, (this.props.data.limit ? this.props.data.limit : 256))
        })
    }

    render(){
        let data = this.props.data;
        return (
            <div data-name={data.name} className="components-textarea">
                {data.icon}
                <textarea name={data.name} placeholder={data.default} defaultValue={this.state[data.name]} onChange={this.handleChange}>{}</textarea >
                {data.check}<span className="counter">{strlen(this.state[data.name])}</span>
            </div>
        );
    }
}

class Input extends Component{

    constructor(props) {
        super(props);
        this.state = {[props.data.name] : props.data.value};
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        this.setState({
            [this.props.data.name]: substr(e.target.value, (this.props.data.limit ? this.props.data.limit : 256))
        })
    }

    render(){
        let data = this.props.data;
        return (
            <div data-name={data.name} className="components-input">
                {data.icon}
                <input type={data.type && 'text'} name={data.name} defaultValue={this.state[data.name]} placeholder={data.default} onChange={this.handleChange}/>
                {data.check}<span className="counter">{strlen(this.state[data.name])}</span>
            </div>
        );
    }
}

class Select extends Component{

    constructor(props) {
        super(props);
        this.state = {[props.data.name] : props.data.default};
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        if(strlen(e.target.value) < this.props.data.limit){
            this.setState({[this.props.data.name]: e.target.value})
        }
    }

    render(){
        let order = 0;
        let data = this.props.data;
        return (
            <div data-name={data.name} className="components-select">
                {data.icon}
                <select name={data.name} defaultValue={this.state[data.name]} onChange={this.handleChange}>
                {data.opts.map((op) => <option key={order++} disabled={op.disable && /disabled/} value={op.value} >{op.desc}</option >)}
                </select >
                {data.check}
            </div>
        );
    }
}

export default class Form extends Component {

    Mid = this.props.forminit;
    submit = this.props.submit;
    change = this.props.change;

    constructor(props) {
        super(props);
        this.state = {};
        this.formSubmit = this.formSubmit.bind(this);
    }

    formSubmit(e) {
        e.map = {
            API: this.Mid.action,
            method: this.Mid.method,
            data: new FormData(e.target),
        };
        e.map.data.append('token', Storage.token);
        e.map.data.append('hash', Storage.hash);
        this.submit(e)
    }


    render() {
        const state = this.state;
        const Makeitem = function (props) {
            let data = props.feature;
            if(state[data.name]){data.value = state[data.name];}
            switch (data.type) {
                case 'textarea': return <Textarea data={data} />;
                case 'select': return <Select data={data} />;
                case 'sec': //return <Makesec data={data} />;
                default : return <Input data={data} />;
            }
        };

        let order = 0;
        return (
            <form className={this.Mid.class} onSubmit={this.formSubmit}>
                {this.Mid.inputs.map((item) => <Makeitem key={order++} feature={item} />)}
                <div className="children" >{this.Mid.children}</div >
                <button className="submit" type="submit" >{Lang.common.submit}</button >
            </form >
        );
    }
}
