import React, { Component } from 'react';
import './main.scss';

function Makeinput(props) {
    let html;
    let arr = props.arr;
    for (let name in arr){
        let data = arr[name];
        let e;
        if(data.type === 'textarea'){
            e = document.createElement(data.type);
            e.name = name;
            html += '<textarea name="'+ name +'" placeholder="' + data.default + '">'+ data.value +'</textarea>';
        } else if (arr[name].type === 'sec') {

        } else {
            html += '<input type="'+ data.type +'" name="'+ name +'" value="'+ data.value +'" placeholder="'+ data.default +'" />';
        }
    }


    return html;
}

export default class Form extends Component {
    render() {

        const Mid = this.props.forminit;

        return (
            <form className={Mid.class} method={Mid.method}  >
                <input name="token" type="hidden" value={Mid.token} />
                <input name="hash" type="hidden" value={Mid.hash} />
                <Makeinput arr={Mid.inputs} />
            </form>
        );
    }
}
