import React, { Component } from 'react';
import './main.scss';

function Makeinput(arr) {
    let html = '';
    for (let name in arr){
        if(arr[name].type === 'textarea'){
            html += '<textarea placeholder="' + arr[name].value + '"></textarea>'
        } else {
            html += '<input type="'+ arr[name].type +'" name="'+ name +'" value="'+ arr[name].value +'" placeholder="'+ arr[name].value +'" />'
        }

    }
}

export default class Form extends Component {
    render() {

        const Mid = this.props.forminit;

        const Inputlist = Mid.inputs.map(
            (item) => <input id={Mid.id + '_' + item} type={item.type} name={item} value={item.value} />
        );

        return (
            <form className={Mid.class} method={Mid.method}  >
                <input name="token" type="hidden" value={Mid.token} />
                <input name="hash" type="hidden" value={Mid.hash} />
                {Inputlist}
            </form>
        );
    }
}
